<?php 	include('../php/Controller/DBclass.php');		$DB = new DB;	$DB->set_database("Lindorff_DB");	$DB->_connect();	$query = "			DROP TABLE [Fcap17]			CREATE TABLE [Fcap17]			(				TaskName nvarchar(50),				NextRunTime nvarchar(50),				Status char(20)			)			BULK			INSERT [Fcap17]			FROM 'C:\\xampp_1.8.2\\htdocs\\Tool\\sql\\data\\fcap17.txt'			WITH			(				FIRSTROW = 2,				FIELDTERMINATOR = ',',				ROWTERMINATOR = '\n'			)						UPDATE [Fcap17]			SET				TaskName = REPLACE(TaskName, '\"', ''),				NextRunTime = REPLACE(NextRunTime, '\"', ''),				Status = REPLACE(Status, '\"', '') 						";	$DB->set_sql($query);	$DB->_query();		$query = "			DROP TABLE [Fcap09]			CREATE TABLE [Fcap09]			(				ID integer,				TaskName nvarchar(50),				NextRunTime nvarchar(50),				Status char(20)			)			BULK			INSERT [Fcap09]			FROM 'C:\\xampp_1.8.2\\htdocs\\Tool\\sql\\data\\fcap09.txt'			WITH			(				FIRSTROW = 2,				FIELDTERMINATOR = ';',				ROWTERMINATOR = '\n'			)						UPDATE [Fcap09]			SET				TaskName = REPLACE(TaskName, '\"', ''),				NextRunTime = REPLACE(NextRunTime, '\"', ''),				Status = REPLACE(Status, '\"', '') 						";	$DB->set_sql($query);	$DB->_query();		$DB->_close();	?>